import React, { useEffect, useState } from 'react'
const Stateslive = () => {

    const [data,setData] = useState([])


const getCovidData = async () => {
const res = await fetch('https://api.covid19india.org/data.json')
const actualData = await res.json()
console.log(actualData.statewise)
setData(actualData.statewise)
} 

    useEffect(() => {
getCovidData()
    },[])

    return(
<>
    <h1 className='heading'>INDIA STATES ðŸ”´LIVE COVID-19</h1>
    <div className='container'>
        <table className='maintable'>
            <thead className='tablehead'>
                <tr>
                    <th>State</th>
                    <th>confirmed</th>
                    <th>Recovered</th>
                    <th>deaths</th>
                    <th>active</th>
                    <th>updated</th>
                </tr>
            </thead>
            <tbody className='tablebody'>
         {   data.map((currElem,index) => {
return(
    <tr>
                    <td>{currElem.state}</td>
                    <td>{currElem.confirmed}</td>
                    <td>{currElem.recovered}</td>
                    <td>{currElem.deaths}</td>
                    <td>{currElem.active}</td>
                    <td>{currElem.lastupdatedtime}</td>
                </tr>
)
            })}
                
            </tbody>
        </table>
    </div>
</>
    )
}
export default Stateslive
